mapper.select:
select 

from operate
where menu = #{menu,jdbcType=INTEGER}
  and op = #{op,jdbcType=VARCHAR}

mapper.delete:
delete from operate
where menu = #{menu,jdbcType=INTEGER}
  and op = #{op,jdbcType=VARCHAR}

mapper.update:
update operate


    name = #{name,jdbcType=VARCHAR},


    sort = #{sort,jdbcType=INTEGER},


    isshow = #{isshow,jdbcType=INTEGER},


where menu = #{menu,jdbcType=INTEGER}
  and op = #{op,jdbcType=VARCHAR}
